DROP TABLE IF EXISTS tracks;
CREATE TABLE tracks ( 
track_id INT NOT NULL PRIMARY KEY NULL AUTO_INCREMENT,
track_title VARCHAR(300) NOT NULL NULL,
track_duration FLOAT NULL NULL,
track_release_year INT NULL NULL,
original_track TINYINT NULL NULL,
original_artist VARCHAR(100) NULL NULL,
track_album VARCHAR(100) NULL NULL,
track_youtube_url VARCHAR(500) NULL NULL,
track_order INT NULL NULL
);
